function ekle(t,e){var n=tinyMCE.activeEditor.selection.getContent()?tinyMCE.activeEditor.selection.getContent():"!!Kod Buraya!!";e.insertContent('<pre class="'+t+'">'+n+"</pre>")}tinymce.PluginManager.add("syntaxhighlighter",function(t){t.addButton("syntaxhighlighter",{type:"menubutton",text:"Kod",icon:!1,menu:[{text:"HTML / XML",onclick:function(){ekle("brush: xml",t)}},{text:"HTML + PHP",onclick:function(){ekle("brush: php ; html-script: true",t)}},{text:"HTML + CSS",onclick:function(){ekle("brush: css ; html-script: true",t)}},{text:"HTML + JavaScript",onclick:function(){ekle("brush: js ; html-script: true",t)}},{text:"PHP",onclick:function(){ekle("brush: php",t)}},{text:"CSS",onclick:function(){ekle("brush: css",t)}},{text:"JavaScript",onclick:function(){ekle("brush: js",t)}},{text:"SQL",onclick:function(){ekle("brush: sql",t)}}]})});